const UNKNOWN = 'Unknown';

var pseudos = [];
var oneUnknown = false;
var persons = msg.payload.body.homes[0].persons;
for(var i=0; i<persons.length; i++) {
    var person = persons[i];
    if (person.out_of_sight === true) {
        continue;
    }
    if (person.pseudo !== undefined) {
        pseudos.push(person.pseudo);
    } else if (oneUnknown === false) {
        oneUnknown = true;
        pseudos.push(UNKNOWN);
    }
}

msg.payload = pseudos;

node.log('[Welcome] ' + pseudos + ' at home');

return msg;

const UNKNOWN = 'Unknown face';

var pseudos = [];
var ids = msg.payload.ids;
var idPseudos = msg.payload.idPseudos;
for(i=0; i<ids.length; i++) {
    var pseudo = idPseudos[ids[i]];
    if (pseudo !== undefined) {
        pseudos.push(pseudo);
    }
}

if (pseudos.length === 0) {
    pseudos.push(UNKNOWN);
}

msg.payload = pseudos;

node.log('[Welcome] ' + pseudos + ' seen');

return msg;

var text = '';
var pseudos = msg.payload;
if (pseudos.length === 0) {
    text = 'nobody\'s';
} else {
    pseudos.sort();
    for(i=0; i<pseudos.length; i++) {
        if (text !== '') {
            if (i === pseudos.length-1) {
                text += ' and ';
            } else {
                text += ', ';
            }
        }
        text += pseudos[i];
    }
    text += ' ';
    text += pseudos.length === 1?'is': 'are';
}
text += ' home';

msg.payload = text;

return msg;

const PIPE_RATE = 10 * 1000;//milliseconds
const DISABLED = '(disabled)';

var pipeCount = msg.delay / PIPE_RATE;
var timer = msg.payload;

var msgs = [];
msgs.push({ reset: true });

if (timer === 'start') {
    for(var i=pipeCount; i>=0; i--) {
        msgs.push({ payload: '|'.repeat(i) });
    }
} else { // timer === 'stop', 'on', 'off'
    var message = '';
    if (flow.get('enabled') === false) {
        message = DISABLED;
    }
    msgs.push({ payload: message });
}

return [ msgs ];

